#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$env_node_path=$env:NODE_PATH
$env:NODE_PATH="C:\invoice\node_modules\.pnpm\html-minifier@3.5.21\node_modules\html-minifier\node_modules;C:\invoice\node_modules\.pnpm\html-minifier@3.5.21\node_modules;C:\invoice\node_modules\.pnpm\node_modules;C:\invoice\node_modules;C:\node_modules;C:\invoice\node_modules\html-minifier\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
} else {
  $env:NODE_PATH="/mnt/c/invoice/node_modules/.pnpm/html-minifier@3.5.21/node_modules/html-minifier/node_modules:/mnt/c/invoice/node_modules/.pnpm/html-minifier@3.5.21/node_modules:/mnt/c/invoice/node_modules/.pnpm/node_modules:/mnt/c/invoice/node_modules:/mnt/c/node_modules:/mnt/c/invoice/node_modules/html-minifier/node_modules"
}
$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../html-minifier/cli.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../html-minifier/cli.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../html-minifier/cli.js" $args
  } else {
    & "node$exe"  "$basedir/../html-minifier/cli.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
